select.if:
select fpt.funding_id AS page_id, fpt.funding_summary_name, fpt.funding_name AS page_title, DATEDIFF(NOW(), fpt.register_date) as recent_sort, DATEDIFF(fpt.end_date, NOW()) as near_deadline_sort, CONCAT( IF(NOW() < fpt.end_date, CONCAT(DATEDIFF(fpt.end_date, NOW()), '일 남음'), IF(DATEDIFF(NOW(), fpt.end_date) = 0, '오늘 마감', '종료')) ) AS event_status, fpt.goal_total, IFNULL(sum(if(ft.funder_id is null, null, rt.reward_price)), 0) as total_reward_price, IFNULL(ROUND((sum(if(ft.funder_id is null, null, rt.reward_price)) / fpt.goal_total) * 100), 0) as join_percent, fpt.main_img_url AS img_url, fpt.funding_category_id from funding_page_tb fpt left outer join reward_tb rt on(rt.funding_id = fpt.funding_id) left outer join funder_tb ft on(ft.reward_id = rt.reward_id) left outer join funding_category_tb fct on(fct.funding_category_id = fpt.funding_category_id)
where DATEDIFF(fpt.end_date, NOW()) >= 0
group by page_id, page_title, event_status
order by recent_sort;
order by total_reward_price DESC;
order by join_percent DESC;
order by DATEDIFF(fpt.end_date, NOW())

select fpt.funding_id AS page_id, fpt.funding_summary_name, fpt.funding_name AS page_title, DATEDIFF(NOW(), fpt.register_date) as recent_sort, DATEDIFF(fpt.end_date, NOW()) as near_deadline_sort, CONCAT( IF(NOW() < fpt.end_date, CONCAT(DATEDIFF(fpt.end_date, NOW()), '일 남음'), IF(DATEDIFF(NOW(), fpt.end_date) = 0, '오늘 마감', '종료')) ) AS event_status, fpt.goal_total, IFNULL(sum(if(ft.funder_id is null, null, rt.reward_price)), 0) as total_reward_price, IFNULL(ROUND((sum(if(ft.funder_id is null, null, rt.reward_price)) / fpt.goal_total) * 100), 0) as join_percent, fpt.main_img_url AS img_url, fpt.funding_category_id from funding_page_tb fpt left outer join reward_tb rt on(rt.funding_id = fpt.funding_id) left outer join funder_tb ft on(ft.reward_id = rt.reward_id) left outer join funding_category_tb fct on(fct.funding_category_id = fpt.funding_category_id) where DATEDIFF(fpt.end_date, NOW()) >= 0 group by page_id, page_title, event_status
order by recent_sort;
order by total_reward_price DESC;
order by join_percent DESC;
order by DATEDIFF(fpt.end_date, NOW())

select fpt.funding_id AS page_id, fpt.funding_summary_name, fpt.funding_name AS page_title, DATEDIFF(NOW(), fpt.register_date) as recent_sort, DATEDIFF(fpt.end_date, NOW()) as near_deadline_sort, CONCAT( IF(NOW() < fpt.end_date, CONCAT(DATEDIFF(fpt.end_date, NOW()), '일 남음'), IF(DATEDIFF(NOW(), fpt.end_date) = 0, '오늘 마감', '종료')) ) AS event_status, fpt.goal_total, IFNULL(sum(if(ft.funder_id is null, null, rt.reward_price)), 0) as total_reward_price, IFNULL(ROUND((sum(if(ft.funder_id is null, null, rt.reward_price)) / fpt.goal_total) * 100), 0) as join_percent, fpt.main_img_url AS img_url, fpt.funding_category_id from funding_page_tb fpt left outer join reward_tb rt on(rt.funding_id = fpt.funding_id) left outer join funder_tb ft on(ft.reward_id = rt.reward_id) left outer join funding_category_tb fct on(fct.funding_category_id = fpt.funding_category_id) where DATEDIFF(fpt.end_date, NOW()) < 0 group by page_id, page_title, event_status
order by recent_sort;
order by total_reward_price DESC;
order by join_percent DESC;
order by DATEDIFF(fpt.end_date, NOW())
